mapper.update:
UPDATE SCHEDULE_USER
SET
TEAMNUMBER = #{teamNumber}
WHERE EMAIL = #{email}

mapper.select:
SELECT EMAIL, NAME, DIVISION, CONVERT(VARCHAR(10), INDATE, 111) as INDATE, TEAMNUMBER FROM SCHEDULE_USER where TEAMNUMBER = #{teamNumber}
and EMAIL LIKE '%${keyWord}%' and NAME LIKE '%${keyWord}%' and DIVISION LIKE '%${keyWord}%'
order by INDATE desc
