mapper.update:
UPDATE SCHEDULE_USER
SET
TeamNumber=#{TeamNumber}
WHERE EMAIL=#{email}

mapper.select:
SELECT EMAIL, NAME, DIVISION, INDATE, TeamNumber FROM SCHEDULE_USER where TeamNumber = #{teamNumber}
and EMAIL LIKE '%${keyWord}%' and NAME LIKE '%${keyWord}%' and DIVISION LIKE '%${keyWord}%'
order by INDATE desc

mapper.update:
UPDATE SCHEDULE_USER
SET
PASSWORD =#{password},
DIVISION =#{division},
TeamNumber = #{TeamNumber}
WHERE EMAIL=#{email}
